package maccess;

public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int r=1;r<=3;r++) {
	for(int c=1;c<=r;c++) {
		System.out.print("* ");
	}
	
	System.out.println();
}

for(int r=1;r<=2;r++) {
	for(int c1=2;c1>=r;c1--) {
		System.out.print("* ");
	}
	System.out.println();
}
	}
	

}
